            //     //         // this.listLabels()
            //     //     }
            //     // }
            //     // window.gapi.signin2.render('loginButton', params)
            // })
        })
    }

    componentDidMount() {
        console.log('Loading')

        this.insertGapiScript();
    }

    login = () => {
        window.gapi.auth2.getAuthInstance().signIn();
    };


    render() {
        return (
            <div>
                <h1>Giigke kigub</h1>

                <button onClick={this.login}>sign in</button>
                <button onClick={this.labels}>messages</button>


            </div>
        );
    }
}

export default Home;
