import {Component} from "react";
import './Home.css';

class Home extends Component {
    insertGapiScript() {
        const script = document.createElement('script')
        script.src = 'https://apis.google.com/js/api.js'
        script.onload = () => {
            this.handleClientLoad()
        }
        script.onreadystatechange = () => {
            if (this.readyState === 'complete') this.onload()
        }
        document.body.appendChild(script)
    }

    handleClientLoad() {
        window.gapi.load('auth2', () => {
            window.gapi.auth2.init({
                apiKey: "AIzaSyCXgkVD9sXXaUzjs4UAGYlHlKEtTWjLqYg",
                clientId: "21821511021-f1vhta3cvkl8lhlep5k8i9anqg2i37eq.apps.googleusercontent.com",
                discoveryDocs: "https://www.googleapis.com/discovery/v1/apis/gmail/v1/rest",
                scope: "https://www.googleapis.com/auth/gmail.readonly",
            });


            //
            // window.gapi.load('signin2', () => {
            //     const params = {
            //         onsuccess: () => {
            //             console.log('User has finished signing in!')
            //             console.log(window.gapi.auth2.getAuthInstance())
            //             // this.listLabels()
            //         }
            //     }
            //     window.gapi.signin2.render('loginButton', params)
            // })
        })
    }

    componentDidMount() {
        console.log('Loading')

        this.insertGapiScript();
    }

    login = () => {
        window.gapi.auth2.getAuthInstance().signIn();
    };

    labels = () => {
        window.gapi.client.gmail.users.labels.list({
          'userId': 'me'
        }).then(function(response) {
          console.log(response.data)
        });
    };



    render() {
        return (
            <div>
                <h1>Giigke kigub</h1>

                <button onClick={this.login}>sign in</button>
                <button onClick={this.labels}>messages</button>



            </div>
        );
    }
}

export default Home;
